import React, { useEffect, useState } from 'react'

function Post() {
    const [error, setError]=useState(null);
    const [isLoaded, setIsLoaded]=useState(false)
    const [postList, setPostList] =useState([]);
    console.log("hallo")
    useEffect(()=>{
        fetch("http://localhost:3030/posts")
        .then(res=>res.json())
        .then(
            (result)=>{
                setIsLoaded(true)
                setPostList(result)
                console.log(result)
            },
            (error)=>{
                setIsLoaded(true)
                setError(error)
            }
        )
    },[])
  if(error){
    return <div>Error !!!</div>
  }else if(!isLoaded){
    return <div>Loading !!!</div>
  }else{
    return(
        <ul>
            {postList.map(post=>(
                <li>
                    {post.title}
                    {post.text} 
                </li>
            ))}
        </ul>
    )
  }
}

export default Post
