import React, { useEffect, useState } from 'react'

function Post() {
    const [error, setError]=useState(null);
    const [isLoaded, setIsLoaded]=useState(false)
    const [postList, setPostList] =useState([]);
    useEffect(()=>{
        fetch('http://localhost:8080/posts', {    
            method: 'GET',    
            withCredentials: true,    
            crossorigin: true,    
            mode: 'no-cors',       
          })
        .then(res=>res.json())

        .then(
            (res)=>{
                console.log("hallo")

                console.log(res)

                setIsLoaded(true)
                setPostList(res)
            },
            (error)=>{
                setIsLoaded(true)
                setError(error)
            }
        )
    },[])
  if(error){
    return <div>Error !!!</div>
  }else if(!isLoaded){
    return <div>Loading !!!</div>
  }else{
    return(
        <ul>
            {postList.map(post=>(
                <li>
                    {post.title}
                    {post.text} 
                </li>
            ))}
        </ul>
    )
  }
}

export default Post
